import React, {Component} from 'react';

import {
    withRouter
  } from 'react-router-dom'
class Dashboardpage extends Component {
  constructor(props) {
    console.log('in Dashboard!')
    super(props);
    this.state = {
        ip:''
    }

  }

  componentDidMount() {

  }
  handleChange = (event) => {
    this.setState({ip: event.target.ip});
  }

  handleSubmit = (event) => {
    alert('A name was submitted: ' + this.state.ip);
    event.preventDefault();
  }
  render() {
    console.log("in landingpage render!!!")
    return(
        <div>

            <form onSubmit={this.handleSubmit}>
                <div class="form-group row">
                    <label for="ip">Ip address</label>
                    <input type="text" class="form-control" id="ip" aria-describedby="ipHelp" name="ip" value={this.state.value} onChange={this.handleChange}/>
                    <small id="ipHelp" class="form-text text-muted">Input Ip to get your current location.</small>
                </div>
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        </div>


    )
  }
}

export default withRouter(Dashboardpage);
